package io;

import java.io.*;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

public class CopyDirectory {
    public static void copyDirectory(String inPath, String outPath)
    throws IOException {
        File fileIn = new File(inPath);
        if (!fileIn.exists())
            fileIn.mkdir();
        if (!fileIn.isDirectory()) {
            System.out.println(inPath + " is not a directory ");
            return;
        }

        File fileOut = new File(outPath);
        if (!fileOut.exists())
            fileOut.mkdir();
        if (!fileOut.isDirectory()) {
            System.out.println(inPath + " is not a directory ");
            return;
        }

        File[] files = fileIn.listFiles();
        if (files.length == 0) return;

        List<File> list = new ArrayList<>();
        Collections.addAll(list, files);

        FileOutputStream out = null;
        FileInputStream in;

        byte[] item = new byte[1024];
        int length;
        try {
            for (int i = 0; i < list.size(); i++) {
                File temp = list.get(i);
                if (temp.isDirectory()) {
                    copyDirectory(temp.getAbsolutePath(),
                    outPath + "\\" + temp.getName());//å½“é‡åˆ°æ–‡ä»¶å¤¹æ—¶ï¼Œé€’å½’è¿›è¡Œæ‹·è´ã€‚
                    continue;
                }
                out = new FileOutputStream(outPath + "\\" + temp.getName());

                in = new FileInputStream(temp);

                while ((length = in.read(item)) != -1)
                    out.write(item, 0, length);
            }
            out.close();
        } catch (FileNotFoundException e) {
            System.out.println("æ–‡ä»¶å¤¹ä¸å­˜åœ¨");
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        System.out.print("è¯·è¾“å…¥ä½ æƒ³è¦æ‹·è´çš„æ–‡ä»¶å¤¹çš„è·¯å¾„ï¼š");
        String inPath = in.readLine();

        System.out.print("\nè¯·è¾“å…¥ä½ æƒ³è¦å­˜æ”¾çš„è·¯å¾„ï¼š");
        String outPath = in.readLine();

        copyDirectory(inPath, outPath);
        System.out.println("æ‹·è´å®Œæˆï¼");
    }
}
