```java
import java.util.Iterator;

public class LinearProbingHashST<Key,Value> {//çº¿æ€§æ¢æµ‹å“ˆå¸Œç¬¦å·è¡¨
    private Key[] keys;
    private Value[] values;
    private int M=16;//çº¿æ€§æ¢æµ‹ç¬¦å·è¡¨çš„æ€»å¤§å°
    private int N;//é”®å€¼å¯¹çš„å¤§å°

    public LinearProbingHashST(){
        keys=(Key[]) new Object[M];
        values=(Value[])new Object[M];
    }

    private int hash(Key key){//è®¡ç®—é”®çš„å“ˆå¸Œå€¼
        return (key.hashCode()&0x7fffffff)%M;
    }

    public int size(){
        return N;
    }

    public void put(Key key,Value value){
        int i=hash(key);
        while(keys[i]!=null){
            if(keys[i].equals(key)) {
                values[i]=value;//å‘½ä¸­åˆ™æ›´æ–°
                return;
            }
            i=(i+1)%M;
        }
        keys[i]=key;//æœªå‘½ä¸­åˆ™æ’å…¥
        values[i]=value;
        N++;
    }

    public Value get(Key key){
        int i=hash(key);
        while(!keys[i].equals(key))
            i=(i+1)%M;
        return values[i];
    }

    public Value delete(Key key){
        Value value;
        int i=hash(key);
        while(!keys[i].equals(key))
            i=(i+1)%M;
        value=values[i];
        for(int k=i;keys[k]!=null;k=(k+1)%M){
            keys[k]=keys[k+1];
            values[k]=values[k+1];
        }
        N--;
        return value;
    }

    public Iterable<Key> keys(){
        return () -> new Iterator<Key>() {
            int i=0;
            @Override
            public boolean hasNext() {
                return i!=keys.length;
            }

            @Override
            public Key next() {
                return keys[i++];
            }
        };
    }

    public static void main(String[] args) {
        LinearProbingHashST<String,Integer> scst=new LinearProbingHashST<>();
        scst.put("1", 1);
        scst.put("2", 2);
        scst.put("3", 3);
        scst.put("4", 4);
        scst.put("5", 5);
        scst.put("6", 6);
        scst.put("12", 1);
        scst.put("23", 2);
        scst.put("34", 3);
        scst.put("45", 4);
        scst.put("56", 5);
        scst.put("67", 6);
        System.out.println(scst.get("2"));
        System.out.println(scst.get("5"));
        System.out.println(scst.get("4"));
        scst.put("100",99);
