## æ‰€æœ‰APIçš„ä»£ç å®ç°
```java
import java.util.LinkedList;
import java.util.Queue;
public class BSTT<Key extends Comparable<Key>,Value> {
    private Node root;
    class Node{
        private Key key;
        private Value val;
        private Node left,right;
        private int N;

        public Node(Key key,Value val,int N){
            this.key=key;
            this.val=val;
            this.N=N;
        }

        public Node(){}
    }

    public int size(){
        return size(root);
    }

    private int size(Node x){
        if(x==null) return 0;
        return x.N;
    }

    public int size(Key lo,Key hi){
        return size(root,lo,hi);
    }

    private int size(Node x,Key lo,Key hi){
        if(x==null) return 0;
        int i=0;
        int cmplo=lo.compareTo(x.key);
        int cmphi=hi.compareTo(x.key);
        if(cmplo<0) i+=size(x.left,lo,hi);
        if(cmplo<=0&&cmphi>=0)  i++;
        if(cmphi>0) i+=size(x.right,lo,hi);
        return i;
    }

    public boolean isEmpty(){
        return root==null;
    }

    public Key min(){
        Node x=min(root);
        return x==null?null:x.key;
    }

    private Node min(Node x){
        if(x==null)   return null;
        if(x.left==null)  return x;
        return min(x.left);
    }

    public Key max(){
        Node x=max(root);
        return x==null?null:x.key;
    }

    private Node max(Node x){
        if(x==null)   return null;
        if(x.right==null)  return x;
        return max(x.right);
    }

    public boolean contains(Key key){
        return contains(root,key)==null?false:true;
    }

    private Node contains(Node x,Key key){
        if(x==null) return null;
        int t=key.compareTo(x.key);
        if(t<0) return contains(x.left,key);
        if(t>0) return contains(x.right,key);
        return x;
    }

    public void put(Key key,Value val){
        root=put(root,key,val);
    }

    private Node put(Node x,Key key,Value val){
        if(x==null) return new Node(key,val,1);
        int t=key.compareTo(x.key);
        if(t<0) x.left=put(x.left,key,val);
        else    if(t>0) x.right=put(x.right,key,val);
        else    x.val=val;
        x.N=1+size(x.left)+size(x.right);
        return x;
    }

    public Value get(Key key){
        Node x=get(root,key);
        return x==null?null:x.val;
    }

    private Node get(Node x,Key key){
        if(x==null) return null;
        int t=key.compareTo(x.key);
        if(t<0) return get(x.left,key);
        if(t>0) return get(x.right,key);
        return x;
    }

    public Key floor(Key key){//è¿”å›å°äºç­‰äºkeyçš„å…ƒç´ çš„é”®å€¼
        Node x=floor(root,key);
        return x==null?null:x.key;
    }

    private Node floor(Node x,Key key){
        if(x==null) return null;
        int t=key.compareTo(x.key);
        if(t==0)    return x;
        else    if(t<0) return floor(x.left,key);
        else {
            Node k=floor(x.right,key);
            return k==null?x:k;
        }
    }

    public Key ceiling(Key key){//è¿”å›å¤§äºç­‰äºkeyçš„å…ƒç´ çš„é”®å€¼
        Node x=ceiling(root,key);
        return x==null?null:x.key;
    }

    private Node ceiling(Node x,Key key){
        if(x==null) return null;
        int t=key.compareTo(x.key);
        if(t==0)    return x;
