```java
public class PriorityQueue<Item extends Comparable<Item>> {
    private Item[] items;
    private int N;

    public PriorityQueue(int maxN) {
        items = (Item[]) new Comparable[maxN + 1];
    }

    public PriorityQueue() {
        items = (Item[]) new Comparable[16];//é»˜è®¤å †å¤§å°ä¸º16
    }

    public boolean isEmpty() {
        return N == 0;
    }

    public int size() {
        return N;
    }

    public void insert(Item item) {
        if(N>items.length*2/3)  resize(items.length,true);
        items[++N] = item;
        swim(N);
    }

    public Item delMax() {
        if(N<items.length/3)  resize(items.length,false);
        Item result = items[1];
        exch(1, N--);
        items[N + 1] = null;
        sink(1);
        return result;
    }

    public int capacity() {
        return items.length;
    }

    private void swim(int k) {
        while (k > 1 && less(k / 2, k)) {
            exch(k / 2, k);
            k /= 2;
        }
    }

    private void sink(int k) {
        while (2 * k <= N) {
            int j = 2 * k;
            if (j < N && less(j, j + 1)) j++;
            if (!less(k, j)) break;
            exch(k, j);
            k = j;
        }
    }

    private void resize(int old, boolean isDilatation) {
    //åˆ¤æ–­æ˜¯æ‰©å®¹(dilatation)è¿˜æ˜¯ç¼©å‡(curtail)
        int current;
        if (isDilatation)//dilatation
            current = old * 2;
        else             //curtail
            current = old / 2;

        Item[] newItems = (Item[]) new Comparable[current];
        System.arraycopy(items,0,newItems,0,N);

        items = newItems;
    }

    private boolean less(int x, int y) {
        return items[x].compareTo(items[y]) < 0;
    }

    private void exch(int x, int y) {
        Item t = items[x];
        items[x] = items[y];
        items[y] = t;
    }

    public static void main(String[] args) {
        Integer[] ints = new Integer[]{23, 547, 234, 92, 56, 23, 2, 52, 5, 234, 5};
        PriorityQueue<Integer> pq = new PriorityQueue<>(3);
        for (Integer c : ints)
            pq.insert(c);
        System.out.println(pq.size());
        System.out.println(pq.isEmpty());
        while (!pq.isEmpty())
            System.out.print(pq.delMax() + "  æ­¤æ—¶å †çš„å®¹é‡ä¸ºï¼š"+pq.capacity()+"\n");
    }
}

