package unit2_4_code;

public class HeapSort {
    public static void sort(Comparable[] a){
        int N=a.length-1;
        for(int k=N/2;k>=0;k--)//å †æ„é€ 
            sink(a,k,N);
        //show(a);
        while(N>0){
            exch(a,0,N--);//ä¿å­˜æ­¤æ—¶å †ä¸­çš„æœ€å¤§å€¼åˆ°Nä¸­
            sink(a,0,N);
        }
    }

    public static boolean less(Comparable[] a,int x,int y){
        return a[x].compareTo(a[y])<0;
    }

    private static void exch(Comparable[] a,int x,int y){
        Comparable t=a[x];
        a[x]=a[y];
        a[y]=t;
    }

    private static void sink(Comparable[] a,int k,int N){
        while(2*k+1<=N){//ä¿è¯kæœ‰å­ç»“ç‚¹
            int j=2*k+1;
            if(j<N&&less(a,j,j+1))    j++;
            if(!less(a,k,j))    break;
            exch(a,k,j);
            k=j;
        }
    }

    private static void show(Comparable[] a){
        for(Comparable b:a)
            System.out.print(b+" ");
        System.out.println();
    }

    public static void main(String[] args){
        Integer[] i={22,31,123,3241,34,1,34,233,22,11,5,74};
        sort(i);
        show(i);
    }
}
