public class Merge extends Sortable {
    private static Comparable[] tmp;

    //é©±åŠ¨ç¨‹åº1
    public static void sort(Comparable[] a) {
        tmp = new Comparable[a.length];//å®šä¹‰ä¸´æ—¶æ•°ç»„
        sort(a, 0, a.length - 1);
    }

    //é©±åŠ¨ç¨‹åº2
    private static void sort(Comparable[] a, int low, int up) {//å°†æ•°ç»„çš„lowåˆ°upæ’åº
        if (low >= up) return;
        int mid = (up + low) / 2;
        sort(a, low, mid);//é€’å½’çš„å¯¹å·¦åŠè¾¹æ’åº
        sort(a, mid + 1, up);//é€’å½’çš„å¯¹å³åŠè¾¹æ’åº
        if (a[mid].compareTo(a[mid + 1]) > 0) merge(a, low, mid, up);
        //å°†ä¸¤åŠè¾¹åˆå¹¶(æ’é™¤æ•°ç»„å·²ç»æœ‰åºçš„æƒ…å†µ)
    }

    //ç®—æ³•æ ¸å¿ƒ
    private static void merge(Comparable[] a, int lo, int mid, int hi) {
        int i = lo, j = mid + 1;
        System.arraycopy(a, lo, tmp, lo, (hi - lo + 1));

        //æ–¹å¼1
        for (int k = lo; k <= hi; k++)
            if (i > mid) a[k] = tmp[j++];
            else if (j > hi) a[k] = tmp[i++];
            else if (tmp[j].compareTo(tmp[i]) == -1) a[k] = tmp[j++];
            else a[k] = tmp[i++];

        //æ–¹å¼2
        int k = lo;
        while (i <= mid && j <= hi)
            if (tmp[j].compareTo(tmp[i]) == -1) a[k++] = tmp[j++];
            else a[k++] = tmp[i++];

        while (i <= mid) a[k++] = tmp[i++];
        while (j <= hi) a[k++] = tmp[j++];

    }

    //æµ‹è¯•ç”¨ä¾‹
    public static void main(String[] args) {
        Integer[] a = {2, 31, 4, 1, 34, 6, 2, 1, 5, 74};
        sort(a);
