            if (tmp[j].compareTo(tmp[i]) == -1) a[k++] = tmp[j++];
            else a[k++] = tmp[i++];

        while (i <= mid) a[k++] = tmp[i++];
        while (j <= hi) a[k++] = tmp[j++];
    }

}
