package Demo;

import java.util.*;

public class BinarySearchST<Key extends Comparable<Key>,Value> {
	private Key[] keys;
	private Value[] values;
	private int N;

	@SuppressWarnings("unchecked")
	public BinarySearchST(int size) {
		keys=(Key[])new Object[size];
		values=(Value[])new Object[size];
	}

	public BinarySearchST() {
		keys=(Key[])new Comparable[30];
		values=(Value[])new Object[30];
	}

	public int capacity() {
		return keys.length;
	}

	public int size() {
		return N;
	}

	public int rank(Key key) {
		/*
		 * æ­¤æ–¹æ³•è¿”å›æ•°ç»„ä¸­å°äºkeyçš„å…ƒç´ çš„ä¸ªæ•°.
		 * å½“keyå°äºæ•°ç»„ä¸­çš„æ‰€æœ‰å…ƒç´ æ—¶,è¿”å›0(æœªå‘½ä¸­)
		 * å½“keyå¤§äºæ•°ç»„ä¸­çš„æ‰€æœ‰å…ƒç´ æ—¶,è¿”å›N(æœªå‘½ä¸­)
		 * å½“keyå°äºæ•°ç»„ä¸­çš„æŸä¸ªæ•°æ—¶,è¿”å›è¿™ä¸ªæ•°çš„ç´¢å¼•(æœªå‘½ä¸­)
		 * å‘½ä¸­æ—¶,è¿”å›keyçš„ç´¢å¼•
		 */
		int lo=0,hi=N-1;
		while(lo<=hi) {
			int mid=(lo+hi)/2;
			int cmp=keys[mid].compareTo(key);
			if(cmp<0)	lo=mid+1;
			else	if(cmp>0)	hi=mid-1;
			else	return mid;
		}
		return lo;
	}

	public void put(Key key,Value value) {
		/*
		 * å¦‚æœvaluewä¸ºnull,åˆ™åˆ é™¤å¯¹åº”çš„key.
		 * å¦‚æœæ•°ç»„ä¸­æœ‰key,åˆ™å°†å…¶valueæ›´æ–°.
		 * å¦‚æœæ²¡æœ‰,å°±æ’å…¥åˆ°æ•°ç»„ä¸­.
		 */
		 if(value==null) {
		     delete(key);
		     return;
		 }
		int position=rank(key);
		if(position<N&&keys[position].compareTo(key)==0){
        //åˆ¤æ–­position<Nçš„ç›®çš„æ—¶é˜²æ­¢position=N,åˆ™åé¢çš„è¯­å¥ä¼šæŠ›å‡ºç©ºæŒ‡æ­£å¼‚å¸¸.
			values[position]=value;
			return;
		}
		for(int j=N;j>position;j--) {
			keys[j]=keys[j-1];
			values[j]=values[j-1];
		}
		keys[position]=key;
		values[position]=value;
		N++;
	}

	public Value get(Key key) {
		int position=rank(key);
		if(position<N&&keys[position].compareTo(key)==0)
        return values[position];
		else	return null;
	}

	public void delete(Key key) {
		int position=rank(key);
		if(position<N&&keys[position].compareTo(key)==0)	{
			for(int j=position+1;j<N;j++) {
				keys[j-1]=keys[j];
				values[j-1]=values[j];
			}
			N--;
		}
	}

	public void deleteAll() {
		for(int i=0;i<N;i++) {
			keys[i]=null;
			values[i]=null;
		}
		N=0;
	}

	public List<Key> keys() {
		List<Key> list=new ArrayList<Key>();
		for(int i=0;i<N;i++)
			list.add(keys[i]);
		return list;
	}

	public Key min() {
		return keys[0];
	}

	public Key max() {
		return keys[N-1];
	}

	public Key select(int index) {
		return keys[index];
	}

	public Key ceiling(Key key) {
		return keys[rank(key)];
	}

	public Key floor(Key key) {
		int i=rank(key);
		return i>=0&&i<=N?keys[i-1]:keys[i];
	}
}

